function getTotalCount() {
    var item = this.parentNode.parentNode.parentNode;
    var checked = $(this).is(':checked');
    var count = parseFloat(item.getElementsByClassName('cc-course-credit')[0].innerHTML);
    var total = parseFloat(document.getElementById('total-credits').innerHTML);
    if (checked) {
        document.getElementById('total-credits').innerHTML = total + count;
        document.getElementById('remain-credits').innerHTML = 25 - total - count;
        $(item).addClass('positive');
    } else {
        document.getElementById('total-credits').innerHTML = total - count;
        document.getElementById('remain-credits').innerHTML = 25 - total + count;
        $(item).removeClass('positive');
    }
}


$(document).ready(function(){
    $('#total-credits').html('0');
    $('#remain-credits').html('25');
    $('.cc-course-checkbox').change(getTotalCount);
});